import React from "react";

import {getSchools} from "../../../API/school";

import "./Schools.css";

class Schools extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            schools: []
        }
    }

    async componentDidMount() {
        const results = await getSchools();
        this.setState({
            schools: results
        });
    }

    tabData() {
        let schools = this.state.schools;

        return schools.map((school, index) => {
            const {name, address, phonenumber} = school;
            let alt = (index % 2) === 1 ? "alt" : "";
            return (
              <tr key={index}>
                  <td className={alt}>{name}</td>
                  <td className={alt}>{address}</td>
                  <td className={alt}>{phonenumber}</td>
              </tr>
            );
        });
    }

    render(){
        return <div id="schools">
            <p id="schoolTitle">SCHOOLS</p>
            <table>
                <thead>
                    <tr><th>Name</th><th>Address</th><th>Phone number</th></tr>
                </thead>
                <tbody>
                    {this.tabData()}
                </tbody>
            </table>
        </div>
    }
}

export default Schools;
