import React from "react";
import "../../CSS/SchoolPanel.css"
import {getSchools} from "../../API/school"

class SchoolPanel extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            schools: []
        };
    }
    async componentDidMount() {
        let schools = await getSchools();
        this.setState({
            schools: schools
        });
    }
    
    tabData(tab) {
        let output = "";
        tab.forEach((school) => {
            output += <tr><td>school.name</td><td>school.address</td><td>school.phonenumber</td></tr>
        });
        return output;
    }

    render() {
        return <div>
            <table>
                <thead>
                    <tr>
                        <td>Name</td>
                        <td>Address</td>
                        <td>Phone number</td>
                    </tr>
                </thead>
                <tbody>
                 {this.tabData(this.state.schools)}
                </tbody>
            </table>
        </div>
    }
}

export default SchoolPanel;
