import axios from 'axios';

const URL_ROLEUSER = "http://localhost:3001/user/role";
const URL_USER = "http://localhost:3001/user";

const getRole = async (token) => {
    try {
        const resp = await axios.get(URL_ROLEUSER,
            {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });
        return resp.data.role;
    } catch(e){
        throw new Error("Error while loading role!");
    }
}

const getUsers = async () => {
    try {
        const resp = await axios.get(URL_USER);
        return resp.data;
    } catch(e){
        throw new Error("Error while loading users");
    }
}

export {getRole, getUsers};
