import React from "react";
import "../../CSS/SchoolPanel.css";
import {getSchools} from "../../API/school";
import deleteIcon from "../../Assets/Images/iconeSupprimer.png";
import editIcon from "../../Assets/Images/iconeModifier.png";

class SchoolPanel extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            schools: []
        };
    }
    async componentDidMount() {
        let schools = await getSchools();
        this.setState({
            schools: schools
        });
    }

    render() {
        return <div>
            <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Address</th>
                        <th>Phone number</th>
                        <th>Edit</th>
                        <th>Delete</th>
                    </tr>
                </thead>
                <tbody>
                    {this.state.schools.map((school, index) => {
                        return (
                          <tr key={index}>
                              <td>{school.name}</td>
                              <td>{school.address}</td>
                              <td>{school.phonenumber}</td>
                              <td><img className="icon" src={editIcon} alt="Edit"/></td>
                              <td><img className="icon" src={deleteIcon} alt="Delete"/></td>
                          </tr>
                        );
                    })}
                </tbody>
            </table>
        </div>
    }
}
/* Bouton ajouter en dessous, voir options -> liste ,modifier dans tab ,Bouton delete dans tab*/

export default SchoolPanel;
