import React from "react";
import "../../CSS/SchoolPanel.css"
import {getSchools} from "../../API/school"

class SchoolPanel extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            schools: ""
        };
    }
    async componentDidMount() {
        let schools = await getSchools();
        this.setState({
            schools: schools
        });
    }

    tabHeader() {
        return <tr><th>Name</th><th>Address</th><th>Phone number</th></tr>;
    }

    tabData(tab) {
        let output = "";
        for(let school in tab) {
            output += <tr><td>{tab[school].name}</td><td>{tab[school].address}</td><td>{tab[school].phonenumber}</td></tr>;
        }
        return output;
    }

    render() {
        return <div>
            <table>
               {this.tabHeader()}
               {this.tabData(this.state.schools)}
            </table>
        </div>
    }
}

export default SchoolPanel;
