import axios from "axios";
import {getHeaders} from "./utils";

const SCHOOL_URL = "109.133.232.252:3001/school";

const getSchools = async () => {
    try {
        const resp = await axios.get(SCHOOL_URL, getHeaders());
        return resp.data;
    } catch (e) {
        throw new Error("Error loading schools, try later !");
    }
}

export {getSchools};
