import React from "react";
import "../../CSS/SchoolPanel.css"
import {getSchools} from "../../API/school"

class SchoolPanel extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            schools: []
        };
    }
    async componentDidMount() {
        let schools = await getSchools();
        this.setState({
            schools: schools
        });
    }

    tabHeader() {
        return <tr><th>Name</th><th>Address</th><th>Phone number</th></tr>;
    }

    tabRow(ind) {
        return <tr><td>{this.state.schools[ind].name}</td><td>this.state.schools[ind].address</td><td>this.state.schools[ind].phonenumber</td></tr>
    }

    render() {
        return <div>
            <table>
               {this.tabHeader()}
                
            </table>
        </div>
    }
}

export default SchoolPanel;
