import React from "react";
import "../../CSS/SchoolPanel.css"
import {getSchools} from "../../API/school"

class SchoolPanel extends React.Component {

    constructor(props) {
        super(props);
        const schools = getSchools();
    }

    tabHeader() {
        return <tr><th>Name</th><th>Address</th><th>Phone number</th></tr>;
    }

    tabData(tab) {
        let output = "";
        for(let school of tab) {
            output += <tr><td>school.name</td><td>school.address</td><td>school.phoneNumber</td></tr>
        }
        return output;
    }

    render() {
        return <div>
            <table>
               {this.tabHeader()}
               {this.tabData(schools)}
            </table>
        </div>
    }
}

export default SchoolPanel;
