import React from "react";

import {getSchools} from "../../../API/school";

import "./Schools.css";

import Options from "./Options";

class Schools extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            schools: [],
            schoolsDisplay: [],
            selectedSchool: -1
        }
    }

    async componentDidMount() {
        const results = await getSchools();
        this.setState({
            schools: results,
            schoolsDisplay: results
        });
    }

    search(e){
        let searchValue = e.target.value;
        if(searchValue === ""){
            let schoolsDisplay = this.state.schools;
            this.setState({usersDisplay: schoolsDisplay});
        } else {
            let schools = this.state.schools;
            let filteredSchools = schools.filter((school) => {
                return school.address.toLowerCase().includes(searchValue)
                    || school.name.toLowerCase().includes(searchValue);
            })
            this.setState({schoolsDisplay: filteredSchools});
        }
    }

    selectSchool(schoolId) {
        this.setState({
            selectedSchool: schoolId
        });
    }

    schoolsTab() {
        let schools = this.state.schoolsDisplay;

        return schools.map((school, index) => {
            const {id, name, address, phonenumber} = school;
            let alt = (index % 2) === 1 ? "alt" : "";
            return (
                <tr key={index}>
                    <td className={alt}>{name}</td>
                    <td className={alt}>{address}</td>
                    <td className={alt}>{phonenumber}</td>
                    <td className={alt}><button className="optionsButton" onClick={() => this.selectSchool(id)}>üóí</button></td>
                    <td className={alt}><button className="editButton">‚úè</button></td>
                    <td className={alt}><button className="deleteButton">üóë</button></td>
                </tr>
            );
        });
    }

    render(){

        let options = null;
        if(this.state.selectedSchool !== -1)
            options = <Options schoolId={this.state.selectedSchool}/>;

        return <div id="schools">
            <p className="schoolTitle">SCHOOLS</p>

            <form className="searchBox">
                <input className="searchInput" type="text" name="" placeholder="Search" onChange={(e) => this.search(e)}/>
                <button className="searchButton" onClick={(e) => e.preventDefault()}>
                    <i className="fa fa-search"/>
                </button>
            </form>

            <table>
                <thead>
                <tr><th>Name</th><th>Address</th><th>Phone number</th><th>Options</th><th>Edit</th><th>Delete</th></tr>
                </thead>
                <tbody>
                {this.schoolsTab()}
                </tbody>
            </table>

            <p id="addSchoolWrapper"><button id="addSchoolButton">Add a school</button></p>

            {options}
        </div>
    }
}

export default Schools;
